mapper.select:
select
start_time countKey,
sum(case when start_city not in

    #{city}

and end_city in

    #{city}

then 1 else 0 end) 'countIn',
sum(case when start_city in

    #{city}

and end_city not in

    #{city}

then 1 else 0 end) 'countOut',
sum(case when start_city in

    #{city}

and end_city in

    #{city}

then 1 else 0 end) 'countInner'
from
(
SELECT from_unixtime(start_time,'%H') start_time , start_city, end_city FROM journey
WHERE start_time BETWEEN unix_timestamp(#{begin}) AND unix_timestamp(#{end})
and start_city in

    #{city}

or end_city in

    #{city}

) dataTable
group by countKey

select
start_time countKey,
